using Microsoft.EntityFrameworkCore;
using Food_Menu.Models;

namespace Food_Menu.Data
{
    public class MenuContext : DbContext
    {
        public MenuContext(DbContextOptions<MenuContext> options) : base(options)
        {
        }

        // Metod sovrascritto per definire il modello del database
        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            // Configurazione della chiave primaria composta per l'entità DishIngredient
            modelBuilder.Entity<DishIngredient>().HasKey(di => new
            {
                di.DishId,
                di.IngredientId
            });

            // Configurazione della relazione uno-a-molti tra Dish e DishIngredient
            modelBuilder.Entity<DishIngredient>().HasOne(d => d.Dish).WithMany(di => di.DishIngredients).HasForeignKey(d => d.DishId);

            // Configurazione della relazione uno-a-molti tra Ingredient e DishIngredient
            modelBuilder.Entity<DishIngredient>().HasOne(i => i.Dish).WithMany(di => di.DishIngredients).HasForeignKey(i => i.DishId);

            // Aggiunta dei dati iniziali per l'entità Dish
            modelBuilder.Entity<Dish>().HasData(
                new Dish
                {
                    Id = 1,
                    Name = "Pizza",
                    Price = 7.59,
                    ImageUrl = "data:image/jpeg;base64,",
                });

            // Aggiunta dei dati iniziali per l'entità Ingredient
            modelBuilder.Entity<Ingredient>().HasData(
                new Ingredient
                {
                    Id = 1,
                    Name = "Tomato Sauce",
                });
            modelBuilder.Entity<Ingredient>().HasData(
                new Ingredient
                {
                    Id = 2,
                    Name = "Mozzarella",
                });

            // Aggiunta dei dati iniziali per l'entità DishIngredient
            modelBuilder.Entity<DishIngredient>().HasData(
                new DishIngredient { DishId = 1, IngredientId = 1 },
                new DishIngredient { DishId = 1, IngredientId = 2 });

            // Chiamata al metod della classe base per completare la configurazione del modello
            base.OnModelCreating(modelBuilder);
        }

        // DbSet per l'entità Dish
        public DbSet<Dish> Dishes { get; set; }

        // DbSet per l'entità Ingredient
        public DbSet<Ingredient> Ingredients { get; set; }

        // DbSet per l'entità DishIngredient
        public DbSet<DishIngredient> DishIngredients { get; set; }
    }
}
